import { useState, useEffect } from 'react';
import { api } from '../../utils/api';

const DashboardOverview = () => {
    const [stats, setStats] = useState({
        members: 0,
        events: 0,
        elections: 0
    });

    useEffect(() => {
        fetchStats();
    }, []);

    const fetchStats = async () => {
        try {
            // We don't have a dedicated stats endpoint, but we can fetch lists and count.
            // Or we can add a stats endpoint. For now, let's fetch lists.
            // This is not efficient for large data, but fine for this scale.

            // Fetch members (Secretary route) - might be restricted.
            // Fetch events (President route) - accessible to all (we just added GET /events).
            // Fetch elections (President route) - accessible to all.

            // Note: Secretary route /secretary/members is restricted to sec/pres/vp.
            // If the current user is a member, they can't see total members count via that route.
            // Let's just show events and elections for now, or mock members count if we can't access it.
            // Or we can add a public stats endpoint.

            const [eventsData, electionsData] = await Promise.all([
                api.get('/president/events').catch(() => []),
                api.get('/president/elections').catch(() => [])
            ]);

            setStats({
                members: 1250, // Placeholder as we can't easily fetch this for everyone without a new endpoint
                events: eventsData.length,
                elections: electionsData.filter(e => e.status === 'ongoing').length
            });
        } catch (error) {
            console.error('Error fetching dashboard stats:', error);
        }
    };

    return (
        <div>
            <h1 style={{ marginBottom: '2rem' }}>Dashboard Overview</h1>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1.5rem' }}>
                <div className="card">
                    <h3>Total Members</h3>
                    <p style={{ fontSize: '2rem', fontWeight: 'bold', color: 'var(--primary)' }}>{stats.members}</p>
                </div>
                <div className="card">
                    <h3>Upcoming Events</h3>
                    <p style={{ fontSize: '2rem', fontWeight: 'bold', color: 'var(--secondary)' }}>{stats.events}</p>
                </div>
                <div className="card">
                    <h3>Active Elections</h3>
                    <p style={{ fontSize: '2rem', fontWeight: 'bold', color: 'var(--accent)' }}>{stats.elections}</p>
                </div>
            </div>
        </div>
    );
};

export default DashboardOverview;

import { useState, useEffect } from 'react';
import { api } from '../../../utils/api';

const Inbox = () => {
    const [messages, setMessages] = useState([]);
    const [selectedMessage, setSelectedMessage] = useState(null);
    const [replyContent, setReplyContent] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchMessages();
    }, []);

    const fetchMessages = async () => {
        try {
            const data = await api.get('/departments/messages');
            setMessages(data);
        } catch (error) {
            console.error('Error fetching messages:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleReply = async (e) => {
        e.preventDefault();
        try {
            await api.post(`/departments/messages/${selectedMessage.id}/reply`, { content: replyContent });
            alert('Reply sent successfully');
            setReplyContent('');
            setSelectedMessage(null);
        } catch (error) {
            alert('Failed to send reply');
        }
    };

    if (loading) return <div>Loading messages...</div>;

    return (
        <div>
            <h2 style={{ marginBottom: '1.5rem' }}>Inbox</h2>
            <div className="card">
                {messages.length === 0 ? (
                    <p>No messages found.</p>
                ) : (
                    messages.map(msg => (
                        <div key={msg.id} style={{
                            padding: '1rem',
                            borderBottom: '1px solid var(--border-color)',
                            cursor: 'pointer',
                            backgroundColor: selectedMessage?.id === msg.id ? 'var(--bg-secondary)' : 'transparent'
                        }} onClick={() => setSelectedMessage(msg)}>
                            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                                <div>
                                    <h4 style={{ marginBottom: '0.25rem' }}>{msg.subject}</h4>
                                    <p style={{ fontSize: '0.875rem', color: 'var(--text-muted)' }}>
                                        From: {msg.sender ? `${msg.sender.firstName} ${msg.sender.lastName}` : msg.senderName || 'Anonymous'}
                                    </p>
                                </div>
                                <span style={{ fontSize: '0.875rem', color: 'var(--text-muted)' }}>
                                    {new Date(msg.createdAt).toLocaleDateString()}
                                </span>
                            </div>
                        </div>
                    ))
                )}
            </div>

            {selectedMessage && (
                <div className="card" style={{ marginTop: '2rem' }}>
                    <h3>Re: {selectedMessage.subject}</h3>
                    <p style={{ margin: '1rem 0', padding: '1rem', backgroundColor: 'var(--bg-secondary)', borderRadius: '0.5rem' }}>
                        {selectedMessage.content}
                    </p>
                    <form onSubmit={handleReply}>
                        <textarea
                            className="input"
                            rows="4"
                            placeholder="Type your reply..."
                            value={replyContent}
                            onChange={(e) => setReplyContent(e.target.value)}
                            required
                            style={{ marginBottom: '1rem' }}
                        ></textarea>
                        <div style={{ display: 'flex', gap: '1rem' }}>
                            <button type="submit" className="btn btn-primary">Send Reply</button>
                            <button type="button" className="btn btn-outline" onClick={() => setSelectedMessage(null)}>Cancel</button>
                        </div>
                    </form>
                </div>
            )}
        </div>
    );
};

export default Inbox;

import { useState, useEffect } from 'react';
import { api } from '../../../utils/api';

const Attendance = () => {
    const [status, setStatus] = useState(null);
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(false);

    const handleCheckIn = async () => {
        setLoading(true);
        try {
            const res = await api.post('/attendance/check-in', {});
            setStatus('success');
            setMessage(res.message);
        } catch (error) {
            setStatus('error');
            setMessage(error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <h2 style={{ marginBottom: '1.5rem' }}>Attendance</h2>
            <div className="card" style={{ textAlign: 'center', padding: '3rem' }}>
                <p style={{ marginBottom: '2rem', fontSize: '1.25rem' }}>
                    Current Time: {new Date().toLocaleTimeString()}
                </p>

                {status === 'success' ? (
                    <div style={{ color: 'green', fontWeight: 'bold', fontSize: '1.5rem' }}>
                        Γ£à {message || 'Checked In'}
                    </div>
                ) : (
                    <>
                        {status === 'error' && (
                            <div style={{ color: 'red', marginBottom: '1rem' }}>
                                Γ¥î {message}
                            </div>
                        )}
                        <button
                            onClick={handleCheckIn}
                            className="btn btn-primary"
                            style={{ fontSize: '1.5rem', padding: '1rem 3rem' }}
                            disabled={loading}
                        >
                            {loading ? 'Checking In...' : 'Check In Now'}
                        </button>
                    </>
                )}

                <p style={{ marginTop: '2rem', color: 'var(--text-muted)' }}>
                    Check-in window: 6:00 AM - 9:00 AM
                </p>
            </div>
        </div>
    );
};

export default Attendance;

