    const fetchDepartmentTasks = async (deptId) => {
        try {
            const res = await api.get(`/president/departments/${deptId}/tasks`);
            setDeptTasks(res.data);
        } catch (error) {
            console.error('Error fetching department tasks:', error);
        }
    };

    const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8'];

    const chartData = departments.map(dept => ({
        name: dept.name,
        members: dept.memberCount,
        tasks: dept.taskCount,
        completed: dept.completedTasks
    }));

    if (loading) return <div className="flex items-center justify-center h-64">Loading departments...</div>;

    return (
        <div className="space-y-6 animate-fade-in">
            <div className="flex justify-between items-center">
                <h2 className="text-3xl font-bold flex items-center gap-3">
                    <Building2 className="text-[var(--primary)]" />
                    Department Oversight
                </h2>
            </div>

            {/* Department Stats */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="card bg-gradient-to-br from-blue-500 to-blue-600 text-white"
                >
                    <div className="flex justify-between items-start mb-4">
                        <Building2 size={32} className="opacity-80" />
                    </div>
                    <h3 className="text-3xl font-bold mb-1">{departments.length}</h3>
                    <p className="text-sm opacity-90">Total Departments</p>
                </motion.div>

                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.1 }}
                    className="card bg-gradient-to-br from-green-500 to-green-600 text-white"
                >
                    <div className="flex justify-between items-start mb-4">
                        <Users size={32} className="opacity-80" />
                    </div>
                    <h3 className="text-3xl font-bold mb-1">
                        {departments.reduce((sum, d) => sum + d.memberCount, 0)}
                    </h3>
                    <p className="text-sm opacity-90">Total Members</p>
                </motion.div>

                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.2 }}
                    className="card bg-gradient-to-br from-purple-500 to-purple-600 text-white"
                >
                    <div className="flex justify-between items-start mb-4">
                        <CheckSquare size={32} className="opacity-80" />
                    </div>
                    <h3 className="text-3xl font-bold mb-1">
                        {departments.reduce((sum, d) => sum + d.taskCount, 0)}
                    </h3>
                    <p className="text-sm opacity-90">Active Tasks</p>
                </motion.div>

                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.3 }}
                    className="card bg-gradient-to-br from-orange-500 to-orange-600 text-white"
                >
                    <div className="flex justify-between items-start mb-4">
                        <DollarSign size={32} className="opacity-80" />
                    </div>
                    <h3 className="text-3xl font-bold mb-1">
                        ${departments.reduce((sum, d) => sum + parseFloat(d.budget || 0), 0).toFixed(0)}
                    </h3>
                    <p className="text-sm opacity-90">Total Budget</p>
                </motion.div>
            </div>

            {/* Department Performance Chart */}
            <div className="card">
                <h3 className="font-bold mb-4 flex items-center gap-2">
                    <Activity size={20} />
                    Department Performance Overview
                </h3>
                <div className="h-80">
                    <ResponsiveContainer width="100%" height="100%">
                        <BarChart data={chartData}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="name" />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <Bar dataKey="members" fill="#0088FE" name="Members" />
                            <Bar dataKey="tasks" fill="#00C49F" name="Tasks" />
                            <Bar dataKey="completed" fill="#FFBB28" name="Completed" />
                        </BarChart>
                    </ResponsiveContainer>
                </div>
            </div>

            {/* Department Cards */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {departments.map((dept, index) => (
                    <motion.div
                        key={dept.id}
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: index * 0.1 }}
                        onClick={() => setSelectedDept(dept)}
                        className={`card cursor-pointer transition-all hover:shadow-xl ${selectedDept?.id === dept.id ? 'ring-2 ring-[var(--primary)]' : ''
                            }`}
                    >
                        <div className="flex justify-between items-start mb-4">
                            <div>
                                <h3 className="font-bold text-lg">{dept.name}</h3>
                                <p className="text-sm text-[var(--text-secondary)] line-clamp-2">
                                    {dept.description || 'No description'}
                                </p>
                            </div>
                            <span className={`badge ${dept.status === 'active' ? 'badge-success' : 'badge-secondary'
                                }`}>
                                {dept.status}
                            </span>
                        </div>

                        <div className="grid grid-cols-2 gap-4 mb-4">
                            <div className="text-center p-3 bg-[var(--bg-secondary)] rounded-lg">
                                <div className="text-2xl font-bold text-blue-600">{dept.memberCount}</div>
                                <div className="text-xs text-[var(--text-secondary)]">Members</div>
                            </div>
                            <div className="text-center p-3 bg-[var(--bg-secondary)] rounded-lg">
                                <div className="text-2xl font-bold text-green-600">{dept.taskCount}</div>
                                <div className="text-xs text-[var(--text-secondary)]">Tasks</div>
                            </div>
                        </div>

                        <div className="flex justify-between items-center pt-4 border-t border-[var(--border-color)]">
                            <div className="text-sm">
                                <span className="text-[var(--text-secondary)]">Budget: </span>
                                <span className="font-bold">${dept.budget || 0}</span>
                            </div>
                            <div className="text-sm">
                                <span className="text-green-600 font-bold">
                                    {dept.taskCount > 0 ? Math.round((dept.completedTasks / dept.taskCount) * 100) : 0}%
                                </span>
                                <span className="text-[var(--text-secondary)]"> complete</span>
                            </div>
                        </div>
                    </motion.div>
                ))}
            </div>

            {/* Department Tasks Detail */}
            {selectedDept && (
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="card"
                >
                    <h3 className="font-bold text-xl mb-4">
                        {selectedDept.name} - Tasks Overview
                    </h3>

                    {deptTasks.length === 0 ? (
                        <p className="text-[var(--text-secondary)]">No tasks assigned to this department.</p>
                    ) : (
                        <div className="overflow-x-auto">
                            <table className="w-full">
                                <thead>
                                    <tr className="border-b border-[var(--border-color)]">
                                        <th className="text-left p-3">Task</th>
                                        <th className="text-left p-3">Assigned To</th>
                                        <th className="text-left p-3">Status</th>
                                        <th className="text-left p-3">Priority</th>
                                        <th className="text-left p-3">Due Date</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {deptTasks.map(task => (
                                        <tr key={task.id} className="border-b border-[var(--border-color)] hover:bg-[var(--bg-secondary)]">
                                            <td className="p-3">
                                                <div className="font-medium">{task.title}</div>
                                                <div className="text-xs text-[var(--text-secondary)] line-clamp-1">
                                                    {task.description}
                                                </div>
                                            </td>
                                            <td className="p-3">
                                                {task.assignee ? `${task.assignee.firstName} ${task.assignee.lastName}` : 'Unassigned'}
                                            </td>
                                            <td className="p-3">
                                                <span className={`badge ${task.status === 'completed' ? 'badge-success' :
                                                        task.status === 'in-progress' ? 'badge-warning' : 'badge-secondary'
                                                    }`}>
                                                    {task.status}
                                                </span>
                                            </td>
                                            <td className="p-3">
                                                <span className={`badge ${task.priority === 'high' ? 'badge-error' :
                                                        task.priority === 'medium' ? 'badge-warning' : 'badge-secondary'
                                                    }`}>
                                                    {task.priority}
                                                </span>
                                            </td>
                                            <td className="p-3 text-sm">
                                                {task.dueDate ? new Date(task.dueDate).toLocaleDateString() : 'No due date'}
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </motion.div>
            )}
        </div>
    );
};

export default Departments;

import { useState, useEffect } from 'react';
import { api } from '../../../utils/api';
import {
    Vote,
    Plus,
    UserPlus,
    Calendar,
    Clock,
    TrendingUp,
    AlertTriangle,
    CheckCircle
} from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import {
    BarChart,
    Bar,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer,
    Cell
} from 'recharts';

const Elections = () => {
    const [elections, setElections] = useState([]);
    const [liveResults, setLiveResults] = useState(null);
    const [view, setView] = useState('manage'); // 'manage', 'live'
    const [formData, setFormData] = useState({ title: '', startDate: '', endDate: '' });
    const [loading, setLoading] = useState(true);
    const [showCandidateModal, setShowCandidateModal] = useState(false);
    const [selectedElection, setSelectedElection] = useState(null);
    const [candidateData, setCandidateData] = useState({ userId: '', position: 'President', manifesto: '', description: '', photo: null });
    const [users, setUsers] = useState([]);
    const [userSearch, setUserSearch] = useState('');

    useEffect(() => {
        fetchElections();
        fetchUsers();
    }, []);

    useEffect(() => {
        let interval;
        if (view === 'live') {
            fetchLiveResults();
            interval = setInterval(fetchLiveResults, 5000); // Poll every 5 seconds
        }
        return () => clearInterval(interval);
    }, [view]);

    const fetchElections = async () => {
        try {
            const data = await api.get('/president/elections');
            setElections(data);
        } catch (error) {
            console.error('Error fetching elections:', error);
        } finally {
            setLoading(false);
        }
    };

    const fetchUsers = async () => {
        try {
            const res = await api.get('/president/members');
            setUsers(res);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    const fetchLiveResults = async () => {
        try {
            const res = await api.get('/president/elections/live');
            setLiveResults(res.data);
        } catch (error) {
            console.error('Error fetching live results:', error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await api.post('/president/elections', formData);
            alert('Election created successfully');
            setElections([res.data, ...elections]);
            setFormData({ title: '', startDate: '', endDate: '' });
        } catch (error) {
            alert('Error creating election: ' + error.message);
        }
    };

    const handleAddCandidate = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('userId', candidateData.userId);
        formData.append('position', candidateData.position);
        formData.append('manifesto', candidateData.manifesto);
        formData.append('description', candidateData.description);
        if (candidateData.photo) {
            formData.append('photo', candidateData.photo);
        }

        try {
            const token = JSON.parse(localStorage.getItem('user')).token;
            const res = await fetch(`http://localhost:5000/api/president/elections/${selectedElection.id}/candidates`, {
                method: 'POST',
                headers: { 'Authorization': `Bearer ${token}` },
                body: formData
            });

            if (!res.ok) throw new Error(await res.text());

            alert('Candidate added successfully');
            setShowCandidateModal(false);
            setCandidateData({ userId: '', position: 'President', manifesto: '', description: '', photo: null });
            fetchElections();
        } catch (error) {
            alert('Error adding candidate: ' + error.message);
        }
    };

    const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8'];

    return (
        <div className="space-y-6 animate-fade-in">
            <div className="flex justify-between items-center">
                <h2 className="text-3xl font-bold flex items-center gap-3">
                    <Vote className="text-[var(--primary)]" />
                    Election Control Center
                </h2>
                <div className="flex gap-2">
                    <button
                        onClick={() => setView('manage')}
                        className={`btn ${view === 'manage' ? 'btn-primary' : 'bg-[var(--bg-card)]'}`}
                    >
                        Manage Elections
                    </button>
                    <button
                        onClick={() => setView('live')}
                        className={`btn ${view === 'live' ? 'btn-primary' : 'bg-[var(--bg-card)]'} flex items-center gap-2`}
                    >
                        <TrendingUp size={18} />
                        Live Monitor
                    </button>
                </div>
            </div>

            {view === 'live' ? (
                <div className="space-y-6">
                    {!liveResults?.active ? (
                        <div className="card text-center py-12">
                            <AlertTriangle size={48} className="mx-auto text-yellow-500 mb-4" />
                            <h3 className="text-xl font-bold">No Active Election</h3>
                            <p className="text-[var(--text-secondary)]">Start an election to see live results.</p>
                        </div>
                    ) : (
                        <>
                            <div className="card bg-gradient-to-r from-[var(--primary)] to-[var(--primary-dark)] text-white">
                                <div className="flex justify-between items-center">
                                    <div>
                                        <h3 className="text-2xl font-bold">{liveResults.election.title}</h3>
                                        <p className="opacity-90">Live Voting in Progress</p>
                                    </div>
                                    <div className="flex items-center gap-2 bg-white/20 px-4 py-2 rounded-full animate-pulse">
                                        <div className="w-3 h-3 bg-red-500 rounded-full"></div>
                                        <span className="font-bold text-sm">LIVE</span>
                                    </div>
                                </div>
                            </div>

                            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                                <motion.div
                                    initial={{ opacity: 0, x: -20 }}
                                    animate={{ opacity: 1, x: 0 }}
                                    className="card"
                                >
                                    <h3 className="font-bold mb-4">Vote Distribution</h3>
                                    <div className="h-80">
                                        <ResponsiveContainer width="100%" height="100%">
                                            <BarChart data={liveResults.results} layout="vertical">
                                                <CartesianGrid strokeDasharray="3 3" />
                                                <XAxis type="number" />
                                                <YAxis dataKey="name" type="category" width={100} />
                                                <Tooltip />
                                                <Bar dataKey="votes" fill="var(--primary)">
                                                    {liveResults.results.map((entry, index) => (
                                                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                                    ))}
                                                </Bar>
                                            </BarChart>
                                        </ResponsiveContainer>
                                    </div>
                                </motion.div>

                                <div className="space-y-4">
                                    {liveResults.results.sort((a, b) => b.votes - a.votes).map((candidate, index) => (
                                        <motion.div
                                            key={candidate.id}
                                            initial={{ opacity: 0, y: 20 }}
                                            animate={{ opacity: 1, y: 0 }}
                                            transition={{ delay: index * 0.1 }}
                                            className="card flex items-center gap-4"
                                        >
                                            <div className="w-12 h-12 rounded-full bg-gray-200 overflow-hidden">
                                                {candidate.photoUrl ? (
                                                    <img src={`http://localhost:5000${candidate.photoUrl}`} alt={candidate.name} className="w-full h-full object-cover" />
                                                ) : (
                                                    <div className="w-full h-full flex items-center justify-center text-gray-500 font-bold">
                                                        {candidate.name[0]}
                                                    </div>
                                                )}
                                            </div>
                                            <div className="flex-1">
                                                <div className="flex justify-between items-center mb-1">
                                                    <h4 className="font-bold">{candidate.name}</h4>
                                                    <span className="text-xl font-bold text-[var(--primary)]">{candidate.votes}</span>
                                                </div>
                                                <div className="w-full bg-gray-200 rounded-full h-2">
                                                    <div
                                                        className="bg-[var(--primary)] h-2 rounded-full transition-all duration-1000"
                                                        style={{ width: `${(candidate.votes / Math.max(...liveResults.results.map(r => r.votes), 1)) * 100}%` }}
                                                    ></div>
                                                </div>
                                                <p className="text-xs text-[var(--text-secondary)] mt-1">{candidate.position}</p>
                                            </div>
                                            {index === 0 && <CheckCircle className="text-green-500" />}
                                        </motion.div>
                                    ))}
                                </div>
                            </div>
                        </>
                    )}
                </div>
            ) : (
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                    <div className="lg:col-span-1 space-y-6">
                        <div className="card border-t-4 border-[var(--primary)]">
                            <h3 className="font-bold mb-4">Create Election</h3>
                            <form onSubmit={handleSubmit} className="space-y-4">
                                <div>
                                    <label className="block mb-1 text-sm font-medium">Title</label>
                                    <input
                                        type="text"
                                        className="input"
                                        value={formData.title}
                                        onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                                        required
                                    />
                                </div>
                                <div>
                                    <label className="block mb-1 text-sm font-medium">Start Date</label>
                                    <input
                                        type="date"
                                        className="input"
                                        value={formData.startDate}
                                        onChange={(e) => setFormData({ ...formData, startDate: e.target.value })}
                                        required
                                    />
                                </div>
                                <div>
                                    <label className="block mb-1 text-sm font-medium">End Date</label>
                                    <input
                                        type="date"
                                        className="input"
                                        value={formData.endDate}
                                        onChange={(e) => setFormData({ ...formData, endDate: e.target.value })}
                                        required
                                    />
                                </div>
                                <button type="submit" className="btn btn-primary w-full flex items-center justify-center gap-2">
                                    <Plus size={18} /> Create Election
                                </button>
                            </form>
                        </div>
                    </div>

                    <div className="lg:col-span-2 space-y-4">
                        <h3 className="font-bold text-lg">Election Timeline</h3>
                        {elections.length === 0 ? (
                            <p className="text-[var(--text-secondary)]">No elections found.</p>
                        ) : (
                            elections.map((election, index) => (
                                <motion.div
                                    key={election.id}
                                    initial={{ opacity: 0, x: 20 }}
                                    animate={{ opacity: 1, x: 0 }}
                                    transition={{ delay: index * 0.1 }}
                                    className="card relative overflow-hidden"
                                >
                                    <div className={`absolute left-0 top-0 bottom-0 w-1 ${election.status === 'ongoing' ? 'bg-green-500' : 'bg-gray-300'}`}></div>
                                    <div className="flex justify-between items-start pl-4">
                                        <div>
                                            <h4 className="font-bold text-lg">{election.title}</h4>
                                            <div className="flex gap-4 text-sm text-[var(--text-secondary)] mt-1">
                                                <span className="flex items-center gap-1"><Calendar size={14} /> {new Date(election.startDate).toLocaleDateString()} - {new Date(election.endDate).toLocaleDateString()}</span>
                                                <span className="flex items-center gap-1"><UserPlus size={14} /> {election.Candidates?.length || 0} Candidates</span>
                                            </div>
                                        </div>
                                        <div className="flex flex-col items-end gap-2">
                                            <span className={`badge ${election.status === 'ongoing' ? 'badge-success' : 'badge-secondary'}`}>
                                                {election.status}
                                            </span>
                                            <button
                                                onClick={() => {
                                                    setSelectedElection(election);
                                                    setShowCandidateModal(true);
                                                }}
                                                className="text-sm text-[var(--primary)] hover:underline flex items-center gap-1"
                                            >
                                                <UserPlus size={14} /> Add Candidate
                                            </button>
                                        </div>
                                    </div>
                                </motion.div>
                            ))
                        )}
                    </div>
                </div>
            )}

            {/* Candidate Modal */}
            <AnimatePresence>
                {showCandidateModal && (
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4"
                    >
                        <motion.div
                            initial={{ scale: 0.9, opacity: 0 }}
                            animate={{ scale: 1, opacity: 1 }}
                            exit={{ scale: 0.9, opacity: 0 }}
                            className="card w-full max-w-lg max-h-[90vh] overflow-y-auto"
                        >
                            <h3 className="font-bold text-xl mb-4">Add Candidate to {selectedElection?.title}</h3>
                            <form onSubmit={handleAddCandidate} className="space-y-4">
                                <div>
                                    <label className="block mb-1 text-sm font-medium">Search & Select User</label>
                                    <input
                                        type="text"
                                        className="input mb-2"
                                        placeholder="Search by name or student ID..."
                                        value={userSearch}
                                        onChange={e => setUserSearch(e.target.value)}
                                    />
                                    <select
                                        className="input"
                                        value={candidateData.userId}
                                        onChange={e => setCandidateData({ ...candidateData, userId: e.target.value })}
                                        required
                                        size="5"
                                        style={{ minHeight: '120px' }}
                                    >
                                        <option value="">Select a user...</option>
                                        {users
                                            .filter(user => {
                                                const searchLower = userSearch.toLowerCase();
                                                return (
                                                    user.firstName?.toLowerCase().includes(searchLower) ||
                                                    user.lastName?.toLowerCase().includes(searchLower) ||
                                                    user.studentId?.toLowerCase().includes(searchLower)
                                                );
                                            })
                                            .map(user => (
                                                <option key={user.id} value={user.id}>
                                                    {user.firstName} {user.lastName} ({user.studentId}) - {user.department || 'No Dept'}
                                                </option>
                                            ))}
                                    </select>
                                    <p className="text-xs text-[var(--text-secondary)] mt-1">
                                        {users.length} total users available
                                    </p>
                                </div>
                                <div>
                                    <label className="block mb-1 text-sm font-medium">Position</label>
                                    <select
                                        className="input"
                                        value={candidateData.position}
                                        onChange={e => setCandidateData({ ...candidateData, position: e.target.value })}
                                    >
                                        <option value="President">President</option>
                                        <option value="Vice President">Vice President</option>
                                        <option value="Secretary">Secretary</option>
                                    </select>
                                </div>
                                <div>
                                    <label className="block mb-1 text-sm font-medium">Manifesto</label>
                                    <textarea
                                        className="input"
                                        value={candidateData.manifesto}
                                        onChange={e => setCandidateData({ ...candidateData, manifesto: e.target.value })}
                                        rows="3"
                                        required
                                    />
                                </div>
                                <div>
                                    <label className="block mb-1 text-sm font-medium">Description</label>
                                    <textarea
                                        className="input"
                                        value={candidateData.description}
                                        onChange={e => setCandidateData({ ...candidateData, description: e.target.value })}
                                        rows="3"
                                        required
                                    />
                                </div>
                                <div>
                                    <label className="block mb-1 text-sm font-medium">Photo</label>
                                    <input
                                        type="file"
                                        className="input"
                                        onChange={e => setCandidateData({ ...candidateData, photo: e.target.files[0] })}
                                        accept="image/*"
                                        required
                                    />
                                </div>
                                <div className="flex justify-end gap-2 pt-4">
                                    <button
                                        type="button"
                                        className="btn bg-gray-200 text-gray-700"
                                        onClick={() => setShowCandidateModal(false)}
                                    >
                                        Cancel
                                    </button>
                                    <button type="submit" className="btn btn-primary">Add Candidate</button>
                                </div>
                            </form>
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>
        </div >
    );
};

export default Elections;

import { useState, useEffect } from 'react';
import { api } from '../../../utils/api';
import {
    Calendar as CalendarIcon,
    Plus,
    Check,
    X,
    MapPin,
    Clock,
    Filter,
    MoreHorizontal
} from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

const Events = () => {
    const [events, setEvents] = useState([]);
    const [formData, setFormData] = useState({ title: '', date: '', time: '', venue: '', description: '' });
    const [loading, setLoading] = useState(true);
    const [view, setView] = useState('list'); // 'list', 'calendar', 'approval'
    const [showForm, setShowForm] = useState(false);

    useEffect(() => {
        fetchEvents();
    }, []);

    const fetchEvents = async () => {
        try {
            const res = await api.get('/president/events');
            setEvents(res.data);
        } catch (error) {
            console.error('Error fetching events:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await api.post('/president/events', formData);
            alert('Event created successfully');
            setEvents([...events, res.data]);
            setFormData({ title: '', date: '', time: '', venue: '', description: '' });
            setShowForm(false);
        } catch (error) {
            alert('Error creating event: ' + error.message);
        }
    };

    const handleStatusUpdate = async (id, status) => {
        try {
            const res = await api.put(`/president/events/${id}/status`, { status });
            setEvents(events.map(e => e.id === id ? res.data : e));
        } catch (error) {
            alert('Error updating status');
        }
    };

    const pendingEvents = events.filter(e => e.status === 'pending');
    const approvedEvents = events.filter(e => e.status === 'approved');

    return (
        <div className="space-y-6 animate-fade-in">
            <div className="flex justify-between items-center">
                <h2 className="text-3xl font-bold flex items-center gap-3">
                    <CalendarIcon className="text-[var(--primary)]" />
                    Event Management
                </h2>
                <div className="flex gap-2">
                    <button
                        onClick={() => setView('list')}
                        className={`btn ${view === 'list' ? 'btn-primary' : 'bg-[var(--bg-card)]'}`}
                    >
                        List
                    </button>
                    <button
                        onClick={() => setView('approval')}
                        className={`btn relative ${view === 'approval' ? 'btn-primary' : 'bg-[var(--bg-card)]'}`}
                    >
                        Approval
                        {pendingEvents.length > 0 && (
                            <span className="absolute -top-1 -right-1 w-5 h-5 bg-red-500 text-white rounded-full text-xs flex items-center justify-center">
                                {pendingEvents.length}
                            </span>
                        )}
                    </button>
                    <button
                        onClick={() => setShowForm(!showForm)}
                        className="btn bg-green-600 text-white flex items-center gap-2"
                    >
                        <Plus size={18} />
                        New Event
                    </button>
                </div>
            </div>

            <AnimatePresence>
                {showForm && (
                    <motion.div
                        initial={{ opacity: 0, height: 0 }}
                        animate={{ opacity: 1, height: 'auto' }}
                        exit={{ opacity: 0, height: 0 }}
                        className="overflow-hidden"
                    >
                        <div className="card mb-6 border border-[var(--primary)]">
                            <h3 className="font-bold mb-4">Create New Event</h3>
                            <form onSubmit={handleSubmit} className="space-y-4">
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                    <div>
                                        <label className="block mb-1 text-sm font-medium">Title</label>
                                        <input
                                            type="text"
                                            className="input"
                                            value={formData.title}
                                            onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                                            required
                                        />
                                    </div>
                                    <div>
                                        <label className="block mb-1 text-sm font-medium">Venue</label>
                                        <input
                                            type="text"
                                            className="input"
                                            value={formData.venue}
                                            onChange={(e) => setFormData({ ...formData, venue: e.target.value })}
                                            required
                                        />
                                    </div>
                                    <div>
                                        <label className="block mb-1 text-sm font-medium">Date</label>
                                        <input
                                            type="date"
                                            className="input"
                                            value={formData.date}
                                            onChange={(e) => setFormData({ ...formData, date: e.target.value })}
                                            required
                                        />
                                    </div>
                                    <div>
                                        <label className="block mb-1 text-sm font-medium">Time</label>
                                        <input
                                            type="time"
                                            className="input"
                                            value={formData.time}
                                            onChange={(e) => setFormData({ ...formData, time: e.target.value })}
                                            required
                                        />
                                    </div>
                                </div>
                                <div>
                                    <label className="block mb-1 text-sm font-medium">Description</label>
                                    <textarea
                                        className="input"
                                        rows="3"
                                        value={formData.description}
                                        onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                                    ></textarea>
                                </div>
                                <div className="flex justify-end gap-2">
                                    <button
                                        type="button"
                                        onClick={() => setShowForm(false)}
                                        className="btn bg-gray-200 text-gray-700"
                                    >
                                        Cancel
                                    </button>
                                    <button type="submit" className="btn btn-primary">Create Event</button>
                                </div>
                            </form>
                        </div>
                    </motion.div>
                )}
            </AnimatePresence>

            {view === 'approval' ? (
                <div className="space-y-4">
                    <h3 className="font-bold text-lg">Pending Approval ({pendingEvents.length})</h3>
                    {pendingEvents.length === 0 ? (
                        <p className="text-[var(--text-secondary)]">No pending events.</p>
                    ) : (
                        pendingEvents.map(event => (
                            <motion.div
                                key={event.id}
                                layout
                                initial={{ opacity: 0 }}
                                animate={{ opacity: 1 }}
                                exit={{ opacity: 0 }}
                                className="card flex flex-col md:flex-row justify-between items-start md:items-center gap-4 border-l-4 border-yellow-500"
                            >
                                <div>
                                    <h4 className="font-bold text-lg">{event.title}</h4>
                                    <div className="flex gap-4 text-sm text-[var(--text-secondary)] mt-1">
                                        <span className="flex items-center gap-1"><CalendarIcon size={14} /> {event.date}</span>
                                        <span className="flex items-center gap-1"><Clock size={14} /> {event.time}</span>
                                        <span className="flex items-center gap-1"><MapPin size={14} /> {event.venue}</span>
                                    </div>
                                    <p className="mt-2 text-sm">{event.description}</p>
                                </div>
                                <div className="flex gap-2">
                                    <button
                                        onClick={() => handleStatusUpdate(event.id, 'approved')}
                                        className="btn bg-green-100 text-green-700 hover:bg-green-200 flex items-center gap-2"
                                    >
                                        <Check size={18} /> Approve
                                    </button>
                                    <button
                                        onClick={() => handleStatusUpdate(event.id, 'rejected')}
                                        className="btn bg-red-100 text-red-700 hover:bg-red-200 flex items-center gap-2"
                                    >
                                        <X size={18} /> Reject
                                    </button>
                                </div>
                            </motion.div>
                        ))
                    )}
                </div>
            ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {approvedEvents.map((event, index) => (
                        <motion.div
                            key={event.id}
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: index * 0.1 }}
                            className="card hover:shadow-lg transition-shadow"
                        >
                            <div className="flex justify-between items-start mb-4">
                                <div className="bg-[var(--bg-secondary)] p-3 rounded-lg text-center min-w-[60px]">
                                    <div className="text-xs font-bold uppercase text-[var(--primary)]">
                                        {new Date(event.date).toLocaleString('default', { month: 'short' })}
                                    </div>
                                    <div className="text-xl font-bold">
                                        {new Date(event.date).getDate()}
                                    </div>
                                </div>
                                <div className={`badge ${event.status === 'approved' ? 'badge-success' : 'badge-warning'}`}>
                                    {event.status}
                                </div>
                            </div>
                            <h4 className="font-bold text-lg mb-2 line-clamp-1">{event.title}</h4>
                            <div className="space-y-2 text-sm text-[var(--text-secondary)] mb-4">
                                <div className="flex items-center gap-2">
                                    <Clock size={16} /> {event.time}
                                </div>
                                <div className="flex items-center gap-2">
                                    <MapPin size={16} /> {event.venue}
                                </div>
                            </div>
                            <div className="flex justify-between items-center pt-4 border-t border-[var(--border-color)]">
                                <span className="text-xs text-[var(--text-secondary)]">
                                    Created by ID: {event.createdBy}
                                </span>
                                <button className="p-2 hover:bg-[var(--bg-secondary)] rounded-full text-[var(--text-secondary)]">
                                    <MoreHorizontal size={20} />
                                </button>
                            </div>
                        </motion.div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Events;

